import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

}

<div class="container full-screen">
    <img src="./assets/images/logo.png" alt="Logo for My Music">
    <div class="options-container">
        <div class="button-options">
            <button class="bg-dark option-button-create" [routerLink]="'/create'">Create</button>
            <button class="bg-dark option-button-artists" [routerLink]="'/list-artists'">Artists</button>
        </div>
    </div>
</div>
