		// this.wasSubmitted = true;
	}

	private parseTracks(rawTracks: string): Track[] {
		const tracks: Track[] = [];
		const lines = rawTracks.split('\n');

		lines.forEach((line, index) => {
			const [title, lyrics, video] = line.split(':');
			tracks.push(new Track(Math.floor(Math.random() * 1000000), index + 1, title, lyrics || '', video || ''));
		});

		return tracks;
	}
}
<div class="create-album-container">
    <div>
        <h1>Create an Album</h1>
    </div>
    <div class="create-album-form-container">
        <div class="form-container" *ngIf="!wasSubmitted">
            <form (ngSubmit)="onSubmitAlbum(createForm)" #createForm="ngForm">
                <div class="horizontal-form-set">
                    <fieldset class="fieldset-left">
                        <div class="form-group">
                            <label for="artist" class="form-label mt-4">Artist</label>
                            <div class="form-group">
                                <input id="artist" type="text"
                                       class="form-control" aria-describedby="helpArtist"
                                       placeholder="Enter the name of the Artist" required
                                       [(ngModel)]="album.Artist" name="artist">
                            </div>

                        </div>
                        <div class="form-group">
                            <label for="album" class="form-label mt-4">Album</label>
                            <input id="album" type="text"
                                   class="form-control" aria-describedby="helpAlbum"
                                   placeholder="Enter the name of the Album" required
                                   [(ngModel)]="album.Title" name="title">
                        </div>
                        <div class="form-group">
                            <label for="description" class="form-label mt-4">Description</label>
                            <textarea id="description" rows="3" class="form-control"
                                      aria-describedby="helpDescription"
                                      placeholder="Enter the description of the Album" required
                                      [(ngModel)]="album.Description" name="description"></textarea>
                        </div>
                        <div class="form-group">
                            <label for="year" class="form-label mt-4">Year</label>
                            <input id="year" type="text"
                                   class="form-control" aria-describedby="helpYear"
                                   placeholder="Enter the year of the Album" required
                                   [(ngModel)]="album.Year" name="year">
                        </div>
                        <div class="form-group">
                            <label for="image" class="form-label mt-4">Image Filename</label>
                            <input id="image"
                                   type="text" class="form-control"
                                   aria-describedby="helpImage"
                                   placeholder="Enter the image filename for the Album"
                                   [(ngModel)]="album.Image" name="image">
                        </div>
                    </fieldset>
                    <fieldset class="fieldset-right">
                        <div class="form-group">
                            <label for="tracks" class="form-label mt-4">Tracks</label>
                            <textarea id="tracks" rows="18" class="form-control"
                                      aria-describedby="helpTracks"
                                      placeholder="Enter each track followed by a return key for the Album"
                                      required [(ngModel)]="tracksRaw" name="tracks" style="resize: none;"></textarea>
                        </div>

                    </fieldset>

                </div>
                <div class="create-button">
                    <button type="submit" class="btn btn-dark">Submit</button>
                </div>

            </form>
        </div>
        <div class="album-created-message" *ngIf="wasSubmitted"><h1>Album was added successfully.</h1></div>
    </div>
</div>
