import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent
{
  title = 'My Music Collection';
  version = "1.0";

  constructor(private router: Router)
  {

  }

  public displayVersion()
  {
    alert(this.title + " Version: " + this.version);
  }

  public displayArtistList()
  {
    this.router.navigate(['list-artists'], { queryParams: { data: new Date()} });
  }
}
<div class="container-fluid">
    <nav class="navbar navbar-expand-xl navbar-dark bg-primary">
        <div class="container-fluid">
            <a class="navbar-brand" href="#">My Music</a>
            <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarColor01"
                    aria-controls="navbarColor01" aria-expanded="false" aria-label="Toggle navigation">
                <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse" id="navbarColor01">
                <ul class="navbar-nav me-auto">
                    <li class="nav-item">
                        <a class="nav-link" href="https://www.gcu.edu">GCU Home Page</a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link" routerLink="/create">Create Album</a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link" (click)="displayArtistList()">Artist List</a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link" (click)="displayVersion()">About</a>
                    </li>
                </ul>
                <form class="d-flex">
                    <input class="form-control me-sm-2" type="search" placeholder="Search">
                    <button class="btn btn-secondary my-2 my-sm-0" type="submit">Search</button>
                </form>
            </div>
        </div>
    </nav>

    <router-outlet></router-outlet>

</div>
